mapper.select:
select t.* from
(select rownum rn,book.* from
(select
 from book_info


        and donater = #{donaterName}



        and id = #{id}



        and bookNo = #{bookNo}



        and bookName = #{bookName}



        and categoryNo = #{categoryNo}



        and imgUrl = #{imageUrl}



        and description = #{description}



        and author = #{author}



        and version = #{version}



        and state = #{state}

 order by id desc) book
where rownum <= #{endIndex}) t
where t.rn > #{startIndex}

select count(id) from book_info


        and donater = #{donaterName}



        and id = #{id}



        and bookNo = #{bookNo}



        and bookName = #{bookName}



        and categoryNo = #{categoryNo}



        and imgUrl = #{imageUrl}



        and description = #{description}



        and author = #{author}



        and version = #{version}



        and state = #{state}

select  from book_info


        and id =#{id}



        and bookName = #{bookName}



        and bookNo = #{bookNo}

select t.* from
(select rownum rn,book.* from
(select
distinct
bookName,
categoryNo,
imgUrl as imageUrl,
description,
author
from book_info

